import React from 'react';

export default function Modules() {
  const modules = [
    {title: 'Monitoring & Historisation', details: ['Ingestion MQTT/REST', 'TimescaleDB', 'Visualisation temps réel']},
    {title: 'Alertes Intelligentes', details: ['Règles composées', 'Anomalies ML', 'Notifications multi-canaux']},
    {title: 'IA & Recommandations', details: ['Modèles besoins hydriques', 'Fertigation optimisée', 'Optimisation énergétique']},
    {title: 'Vision Artificielle', details: ['CNN pour maladies', 'Comptage fruits', 'Suivi croissance']},
    {title: 'Gestion Économique', details: ['Simulations what-if','KPIs: marges, coûts/ha','Planification trésorerie']},
    {title: 'Chatbot IA', details: ['RAG', 'Multilingue FR/AR/Pulaar', 'Intégration API données']}
  ];
  return (
    <section id="modules" className="py-12">
      <h3 className="text-2xl font-semibold mb-6">Modules fonctionnels</h3>
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {modules.map((m, i) => (
          <div key={i} className="bg-white p-5 rounded card-shadow">
            <h4 className="text-green-700 font-semibold mb-2">{m.title}</h4>
            <ul className="text-sm text-gray-700 list-disc list-inside">
              {m.details.map((d, j) => <li key={j}>{d}</li>)}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
}
