// App.js
import React from "react";
import "./index.css";

const Hero = () => (
  <section className="hero">
    <h1>AgriTech Mauritanie</h1>
    <p>Optimisation intelligente des exploitations agricoles grâce à l'IA et l'IoT</p>
    <button className="cta">Découvrir la plateforme</button>
  </section>
);

const Card = ({ title, children }) => (
  <div className="card">
    <h3>{title}</h3>
    <div>{children}</div>
  </div>
);

const Section = ({ title, cards }) => (
  <section className="section">
    <h2>{title}</h2>
    <div className="cards-container">
      {cards.map((card, idx) => (
        <Card key={idx} title={card.title}>{card.content}</Card>
      ))}
    </div>
  </section>
);

const App = () => {
  const objectifsCards = [
    { title: "Monitoring & Historisation", content: "Centraliser et analyser les données agro-environnementales en temps réel." },
    { title: "Alertes intelligentes", content: "Détection précoce des anomalies et risques pour vos cultures." },
    { title: "Aide à la décision", content: "Recommandations optimisées d'irrigation et fertigation." },
    { title: "Gestion économique", content: "Simulations de rentabilité et optimisation des cultures." },
    { title: "Chatbot IA", content: "Support intelligent 24/7 et assistance personnalisée." },
    { title: "Vision artificielle", content: "Analyse automatique des maladies et suivi de croissance." },
  ];

  return (
    <div className="app">
      <Hero />
      <Section title="Objectifs de la plateforme" cards={objectifsCards} />
      <Section title="Lexique & Définitions" cards={[
        { title: "AgriTech", content: "Technologies pour améliorer la productivité agricole." },
        { title: "IoT", content: "Objets connectés collectant et échangeant des données." },
        { title: "IA", content: "Techniques permettant aux machines d'imiter l'intelligence humaine." }
      ]} />
      <footer className="footer">
        <p>© 2025 AgriTech Mauritanie. Tous droits réservés.</p>
      </footer>
    </div>
  );
};

export default App;
