import React from "react";

export default function App() {
  return (
    <div className="font-sans text-gray-800">
      {/* HEADER */}
      <header className="fixed w-full top-0 z-50 bg-white bg-opacity-90 backdrop-blur-sm shadow-sm">
        <div className="max-w-7xl mx-auto flex justify-between items-center px-6 py-4">
          <h1 className="text-2xl font-bold text-[#0AB998]">Agro<span className="text-[#009100]">Tech</span></h1>
          <nav className="hidden md:flex space-x-8 text-sm font-medium">
            <a href="#how" className="hover:text-[#0AB998]">How It Works</a>
            <a href="#resources" className="hover:text-[#0AB998]">Resources</a>
            <a href="#about" className="hover:text-[#0AB998]">About Us</a>
            <a href="#contact" className="hover:text-[#0AB998]">Contact</a>
          </nav>
          <button className="bg-[#FFBD1F] text-gray-900 font-semibold px-4 py-2 rounded-md shadow hover:brightness-95 transition">
            Request a Demo
          </button>
        </div>
      </header>

      {/* HERO */}
      <section
        className="relative min-h-screen flex items-center justify-center bg-cover bg-center text-white"
        style={{
          backgroundImage:
            "url('https://images.unsplash.com/photo-1501004318641-b39e6451bec6?auto=format&fit=crop&w=1950&q=80')",
        }}
      >
        <div className="absolute inset-0 bg-black bg-opacity-40"></div>
        <div className="relative z-10 text-center px-6">
          <h2 className="text-4xl md:text-5xl font-extrabold mb-4">
            Revolutionize Your Farm with <br />
            <span className="text-[#FFBD1F]">Smart Agro-Tech Solutions</span>
          </h2>
          <p className="max-w-2xl mx-auto text-lg text-gray-100 mb-8">
            Harness real-time data, AI-driven insights, and IoT-powered devices
            to boost crop yields, reduce waste, and make sustainable farming
            decisions faster and smarter.
          </p>
          <button className="bg-[#FFBD1F] text-gray-900 font-semibold px-6 py-3 rounded-md shadow hover:brightness-95 transition">
            Get Your Free Demo
          </button>
        </div>
      </section>

      {/* CONTEXTE ET JUSTIFICATION */}
      <section id="about" className="max-w-6xl mx-auto py-20 px-6">
        <h3 className="text-3xl font-bold text-[#009100] mb-6">
          1. CONTEXTE ET JUSTIFICATION
        </h3>

        <div className="space-y-8">
          <div>
            <h4 className="font-semibold text-xl text-[#0AB998] mb-2">
              1.1 Contexte g√©n√©ral
            </h4>
            <p>
              La R√©publique Islamique de Mauritanie, avec son climat sah√©lien et
              ses d√©fis hydriques croissants, fait face √† des enjeux majeurs en
              mati√®re de s√©curit√© alimentaire et d'optimisation des ressources
              agricoles. L'agriculture moderne n√©cessite une approche
              data-driven pour maximiser les rendements tout en pr√©servant les
              ressources naturelles limit√©es.
            </p>
          </div>

          <div>
            <h4 className="font-semibold text-xl text-[#0AB998] mb-2">
              1.2 Contexte sp√©cifique
            </h4>
            <p>
              Les exploitations agricoles mauritaniennes, qu'elles soient en
              agriculture sous serre ou en plein champ, manquent d'outils
              technologiques int√©gr√©s permettant une gestion optimis√©e des
              ressources (eau, engrais, √©nergie) et une d√©tection pr√©coce des
              risques phytosanitaires.
            </p>
          </div>

          <div>
            <h4 className="font-semibold text-xl text-[#0AB998] mb-2">
              1.3 Probl√©matique
            </h4>
            <ul className="list-disc list-inside space-y-1">
              <li>Manque de visibilit√© en temps r√©el sur les conditions agro-environnementales</li>
              <li>Absence d'outils d'aide √† la d√©cision bas√©s sur les donn√©es</li>
              <li>Gestion sous-optimale des ressources (eau, fertilisants, √©nergie)</li>
              <li>D√©tection tardive des probl√®mes phytosanitaires</li>
              <li>Difficult√©s de tra√ßabilit√© et de conformit√©</li>
              <li>Planification √©conomique insuffisante des exploitations</li>
            </ul>
          </div>
        </div>
      </section>

      {/* OBJECTIFS */}
      <section id="how" className="bg-[#F9FFF9] py-20 px-6">
        <div className="max-w-6xl mx-auto">
          <h3 className="text-3xl font-bold text-[#009100] mb-6">3. OBJECTIFS</h3>
          <p className="mb-6">
            D√©velopper une plateforme logicielle int√©gr√©e de gestion intelligente
            des exploitations agricoles, permettant l'optimisation des ressources,
            la d√©tection pr√©coce des risques et l'aide √† la d√©cision bas√©e sur
            l'analyse de donn√©es en temps r√©el.
          </p>

          <h4 className="font-semibold text-xl text-[#0AB998] mb-2">
            3.2 Objectifs sp√©cifiques :
          </h4>
          <ul className="list-disc list-inside space-y-1">
            <li>Monitoring et historisation : Centraliser et analyser les donn√©es agro-environnementales</li>
            <li>Alertes intelligentes : D√©tecter pr√©cocement les risques et anomalies</li>
            <li>Aide √† la d√©cision : Fournir des recommandations optimis√©es</li>
            <li>Gestion √©conomique : Simuler et optimiser la rentabilit√© des exploitations</li>
            <li>Assistance conversationnelle : Offrir un support 24/7 via chatbot IA</li>
            <li>Vision artificielle : D√©tection automatique des maladies et suivi de croissance</li>
            <li>Tra√ßabilit√© : Assurer la conformit√© et la documentation</li>
          </ul>
        </div>
      </section>

      {/* CARACT√âRISTIQUES */}
      <section id="resources" className="max-w-6xl mx-auto py-20 px-6">
        <h3 className="text-3xl font-bold text-[#009100] mb-6">
          4. CARACT√âRISTIQUES FONCTIONNELLES
        </h3>
        <p className="text-gray-700">
          La plateforme regroupe plusieurs modules compl√©mentaires : monitoring,
          alertes, intelligence artificielle, gestion √©conomique, vision
          artificielle et chatbot. Chaque module vise √† automatiser et am√©liorer
          les d√©cisions agricoles.
        </p>
      </section>

      {/* FOOTER */}
      <footer className="bg-[#009100] text-white py-8">
        <div className="max-w-6xl mx-auto px-6 flex flex-col md:flex-row justify-between items-center">
          <p>¬© 2025 AgriTech Mauritanie. Tous droits r√©serv√©s.</p>
          <div className="flex space-x-6 mt-4 md:mt-0">
            <a href="#about" className="hover:text-[#FFBD1F]">√Ä propos</a>
            <a href="#contact" className="hover:text-[#FFBD1F]">Contact</a>
            <a href="#resources" className="hover:text-[#FFBD1F]">Ressources</a>
          </div>
        </div>
      </footer>
    </div>
  );
}














