import React from "react";

export default function Hero() {
  return (
    <header className="bg-green-50 py-20 text-center">
      <h1 className="text-5xl font-bold mb-4">Agrotech Platform</h1>
      <p className="text-lg text-gray-700 max-w-2xl mx-auto mb-6">
        Gestion intelligente des exploitations agricoles en Mauritanie
      </p>
      <button className="px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition">
        Commencer
      </button>
      <div className="hero-illustration mt-12 mx-auto w-4/5 h-64 bg-green-100 rounded-xl"></div>
    </header>
  );
}
