import React from 'react';

export default function Features() {
  const items = [
    {title: 'Monitoring temps réel', desc: 'Collecte multi-protocoles: MQTT, HTTP, WebSockets. Variables: Température, Humidité, pH, EC, NPK, CO2.'},
    {title: 'Historisation & stockage', desc: 'TimescaleDB pour séries temporelles, agrégations et rétention configurable.'},
    {title: 'Alertes intelligentes', desc: 'Seuils, règles composées, ML pour détection d\'anomalies et escalades multi-canaux.'},
    {title: 'IA & recommandations', desc: 'Irrigation, fertigation, optimisation énergétique et scénarios what-if.'}
  ];
  return (
    <section id="features" className="py-12">
      <h3 className="text-2xl font-semibold mb-6">Caractéristiques principales</h3>
      <div className="grid md:grid-cols-2 gap-6">
        {items.map((it, idx) => (
          <div key={idx} className="bg-white p-5 rounded card-shadow">
            <h4 className="text-lg font-semibold text-green-700 mb-2">{it.title}</h4>
            <p className="text-gray-700 text-sm">{it.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
