import React, { useState, useEffect } from "react";
import {
  Leaf,
  Droplets,
  Brain,
  TrendingUp,
  Camera,
  MessageSquare,
  Bell,
  BarChart3,
  Thermometer,
  Wind,
  Sun,
  Activity,
  Zap,
  Shield,
  ChevronRight,
  Menu,
  X,
  Play,
} from "lucide-react";

export default function AgriTechLanding() {
  const [scrolled, setScrolled] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeModule, setActiveModule] = useState(0);

  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 50);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveModule((prev) => (prev + 1) % 6);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  const features = [
    {
      icon: Activity,
      title: "Monitoring Temps R√©el",
      description:
        "Surveillance continue des param√®tres agro-environnementaux avec alertes intelligentes",
      color: "from-emerald-500 to-teal-600",
      stats: "Donn√©es 24/7",
    },
    {
      icon: Brain,
      title: "Intelligence Artificielle",
      description:
        "Recommandations optimis√©es pour irrigation, fertigation et gestion climatique",
      color: "from-blue-500 to-indigo-600",
      stats: "IA Pr√©dictive",
    },
    {
      icon: Camera,
      title: "Vision Artificielle",
      description:
        "D√©tection automatique des maladies, ravageurs et suivi de croissance",
      color: "from-purple-500 to-pink-600",
      stats: "Analyse Images",
    },
    {
      icon: TrendingUp,
      title: "Gestion √âconomique",
      description: "Simulation et optimisation de la rentabilit√© des exploitations",
      color: "from-orange-500 to-red-600",
      stats: "ROI Optimis√©",
    },
    {
      icon: MessageSquare,
      title: "Chatbot IA 24/7",
      description:
        "Assistant conversationnel multilingue avec support technique intelligent",
      color: "from-cyan-500 to-blue-600",
      stats: "Support Continu",
    },
    {
      icon: Bell,
      title: "Alertes Intelligentes",
      description: "D√©tection pr√©coce des risques et notifications multi-canaux",
      color: "from-rose-500 to-red-600",
      stats: "Pr√©vention Active",
    },
  ];

  const modules = [
    {
      title: "Monitoring & Historisation",
      icon: BarChart3,
      points: [
        "Collecte multi-protocoles (MQTT, REST, WebSockets)",
        "Variables air, sol, syst√®me en temps r√©el",
        "TimescaleDB pour s√©ries temporelles haute performance",
        "Dashboards interactifs et cartes g√©olocalis√©es",
      ],
    },
    {
      title: "Alertes Intelligentes",
      icon: Bell,
      points: [
        "Moteur de r√®gles compos√©es et ML",
        "D√©tection stress hydrique et thermique",
        "Pr√©vention risques phytosanitaires",
        "Notifications multi-canaux avec escalade",
      ],
    },
    {
      title: "IA & Recommandations",
      icon: Brain,
      points: [
        "Mod√®les pr√©dictifs besoins hydriques",
        "Optimisation fertigation NPK temps r√©el",
        "Gestion climatique intelligente (CO‚ÇÇ, √©clairage)",
        "Algorithmes g√©n√©tiques multi-objectifs",
      ],
    },
    {
      title: "Vision Artificielle",
      icon: Camera,
      points: [
        "CNN pour d√©tection maladies foliaires",
        "Suivi croissance et mesures biom√©triques",
        "Comptage fruits et estimation rendements",
        "Classification qualit√© r√©colte automatis√©e",
      ],
    },
    {
      title: "Gestion √âconomique",
      icon: TrendingUp,
      points: [
        "Mod√®les technico-√©conomiques d√©taill√©s",
        "Sc√©narios what-if et optimisation assolement",
        "KPIs techniques et √©conomiques",
        "Benchmarking sectoriel anonymis√©",
      ],
    },
    {
      title: "Chatbot IA Conversationnel",
      icon: MessageSquare,
      points: [
        "RAG (Retrieval-Augmented Generation)",
        "Multilingue : Fran√ßais, Arabe, Pulaar",
        "Acc√®s direct aux donn√©es d'exploitation",
        "Formation interactive et support 24/7",
      ],
    },
  ];

  const stats = [
    { value: "100%", label: "Temps R√©el", icon: Zap },
    { value: "10 ans", label: "Historique", icon: Activity },
    { value: "24/7", label: "Monitoring", icon: Shield },
    { value: "Multi-IoT", label: "Protocoles", icon: Thermometer },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-950 via-emerald-950 to-slate-950 text-white overflow-hidden">
      {/* Navigation */}
      <nav
        className={`fixed w-full z-50 transition-all duration-300 ${scrolled
            ? "bg-slate-950/95 backdrop-blur-lg shadow-xl"
            : "bg-transparent"
          }`}
      >
        <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-br from-emerald-400 to-teal-500 rounded-xl flex items-center justify-center transform hover:scale-110 transition-transform">
              <Leaf className="w-6 h-6 text-white" />
            </div>
            <span className="text-2xl font-bold bg-gradient-to-r from-emerald-400 to-teal-400 bg-clip-text text-transparent">
              AgriTech Mauritanie
            </span>
          </div>

          <div className="hidden md:flex items-center gap-8">
            <a href="#features" className="hover:text-emerald-400 transition-colors">
              Fonctionnalit√©s
            </a>
            <a href="#modules" className="hover:text-emerald-400 transition-colors">
              Modules
            </a>
            <a href="#benefits" className="hover:text-emerald-400 transition-colors">
              Avantages
            </a>
            <button className="px-6 py-2 bg-gradient-to-r from-emerald-500 to-teal-600 rounded-full hover:shadow-lg hover:shadow-emerald-500/50 transition-all transform hover:scale-105">
              D√©mo Gratuite
            </button>
          </div>

          <button className="md:hidden" onClick={() => setMobileMenuOpen(!mobileMenuOpen)}>
            {mobileMenuOpen ? <X /> : <Menu />}
          </button>
        </div>
      </nav>

      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <div className="fixed inset-0 z-40 bg-slate-950/98 backdrop-blur-lg md:hidden pt-20">
          <div className="flex flex-col items-center gap-8 p-8">
            <a href="#features" className="text-xl hover:text-emerald-400 transition-colors">
              Fonctionnalit√©s
            </a>
            <a href="#modules" className="text-xl hover:text-emerald-400 transition-colors">
              Modules
            </a>
            <a href="#benefits" className="text-xl hover:text-emerald-400 transition-colors">
              Avantages
            </a>
            <button className="px-8 py-3 bg-gradient-to-r from-emerald-500 to-teal-600 rounded-full text-lg">
              D√©mo Gratuite
            </button>
          </div>
        </div>
      )}

      {/* Hero Section */}
      <section className="relative pt-32 pb-20 px-6">
        <div className="max-w-7xl mx-auto grid lg:grid-cols-2 gap-12 items-center">
          <div className="space-y-8">
            <div className="inline-block px-4 py-2 bg-emerald-500/20 border border-emerald-500/50 rounded-full text-emerald-400 text-sm font-semibold">
              üöÄ R√©volutionnez Votre Agriculture
            </div>
            <h1 className="text-5xl md:text-6xl lg:text-7xl font-bold leading-tight">
              Gestion Intelligente
              <span className="block bg-gradient-to-r from-emerald-400 via-teal-400 to-cyan-400 bg-clip-text text-transparent">
                de Votre Exploitation
              </span>
            </h1>
            <p className="text-xl text-slate-300 leading-relaxed">
              Plateforme AgriTech compl√®te avec IoT, IA et vision artificielle pour optimiser vos ressources, d√©tecter les risques pr√©cocement et maximiser vos rendements en Mauritanie.
            </p>

            <div className="flex flex-wrap gap-4">
              <button className="group px-8 py-4 bg-gradient-to-r from-emerald-500 to-teal-600 rounded-full font-semibold hover:shadow-2xl hover:shadow-emerald-500/50 transition-all transform hover:scale-105 flex items-center gap-2">
                Commencer Gratuitement
                <ChevronRight className="w-5 h-5 group-hover:translate-x-1 transition-transform" />
              </button>
              <button className="px-8 py-4 bg-white/10 backdrop-blur-sm border border-white/20 rounded-full font-semibold hover:bg-white/20 transition-all flex items-center gap-2">
                <Play className="w-5 h-5" />
                Voir la D√©mo
              </button>
            </div>
          </div>

          {/* Dashboard preview */}
          <div className="relative w-full h-[500px] bg-gradient-to-br from-emerald-500/20 to-teal-500/20 rounded-3xl backdrop-blur-sm border border-white/10 p-8 overflow-hidden">
            <div className="grid grid-cols-2 gap-4">
              {[
                { icon: Thermometer, label: "Temp√©rature", value: "28¬∞C", color: "text-orange-400" },
                { icon: Droplets, label: "Humidit√© Sol", value: "65%", color: "text-blue-400" },
                { icon: Sun, label: "Luminosit√©", value: "850 lux", color: "text-yellow-400" },
                { icon: Wind, label: "CO‚ÇÇ", value: "420 ppm", color: "text-purple-400" }
              ].map((metric, idx) => {
                const MetricIcon = metric.icon;
                return (
                  <div key={idx} className="bg-white/5 backdrop-blur-sm rounded-xl p-4 border border-white/10 hover:scale-105 transition-transform">
                    <MetricIcon className={`w-6 h-6 mb-2 ${metric.color}`} />
                    <div className="text-xs text-slate-400">{metric.label}</div>
                    <div className="text-xl font-bold">{metric.value}</div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section id="features" className="py-20 px-6">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-16">
            <h2 className="text-4xl md:text-5xl font-bold mb-4">
              Fonctionnalit√©s
              <span className="block bg-gradient-to-r from-emerald-400 to-teal-400 bg-clip-text text-transparent">
                Compl√®tes & Int√©gr√©es
              </span>
            </h2>
            <p className="text-xl text-slate-300 max-w-3xl mx-auto">
              Une suite d'outils puissants pour transformer votre exploitation agricole en ferme intelligente et connect√©e
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
            {features.map((feature, idx) => {
              const FeatureIcon = feature.icon;
              return (
                <div key={idx} className="group relative bg-gradient-to-br from-white/5 to-white/10 backdrop-blur-sm rounded-2xl p-8 border border-white/10 hover:border-emerald-500/50 transition-all duration-300 hover:scale-105 hover:shadow-2xl hover:shadow-emerald-500/20">
                  <div className={`w-16 h-16 bg-gradient-to-br ${feature.color} rounded-2xl flex items-center justify-center mb-6 group-hover:scale-110 transition-transform`}>
                    <FeatureIcon className="w-8 h-8 text-white" />
                  </div>
                  <h3 className="text-2xl font-bold mb-3">{feature.title}</h3>
                  <p className="text-slate-300 mb-4">{feature.description}</p>
                  <div className="flex items-center justify-between pt-4 border-t border-white/10">
                    <span className="text-sm text-emerald-400 font-semibold">{feature.stats}</span>
                    <ChevronRight className="w-5 h-5 text-emerald-400 group-hover:translate-x-2 transition-transform" />
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </section>

      {/* Modules Section */}
      <section id="modules" className="py-20 px-6 bg-gradient-to-b from-transparent to-slate-950/50">
        <div className="max-w-7xl mx-auto text-center">
          <h2 className="text-4xl md:text-5xl font-bold mb-4">
            Modules
            <span className="block bg-gradient-to-r from-emerald-400 to-teal-400 bg-clip-text text-transparent">
              Techniques D√©taill√©s
            </span>
          </h2>
          <p className="text-xl text-slate-300 mb-12">Architecture modulaire pour une personnalisation compl√®te</p>

          <div className="grid lg:grid-cols-2 gap-8">
            <div className="space-y-4">
              {modules.map((module, idx) => {
                const ModuleIcon = module.icon;
                return (
                  <div
                    key={idx}
                    onClick={() => setActiveModule(idx)}
                    className={`p-6 rounded-2xl cursor-pointer transition-all duration-300 ${activeModule === idx
                        ? "bg-gradient-to-r from-emerald-500/20 to-teal-500/20 border-2 border-emerald-500 shadow-lg shadow-emerald-500/20"
                        : "bg-white/5 border border-white/10 hover:border-emerald-500/30"
                      }`}
                  >
                    <div className="flex items-center gap-4">
                      <div
                        className={`w-12 h-12 rounded-xl flex items-center justify-center ${activeModule === idx
                            ? "bg-gradient-to-br from-emerald-500 to-teal-600"
                            : "bg-white/10"
                          }`}
                      >
                        <ModuleIcon className="w-6 h-6" />
                      </div>
                      <h3 className="text-xl font-bold">{module.title}</h3>
                    </div>
                  </div>
                );
              })}
            </div>

            <div className="bg-gradient-to-br from-white/5 to-white/10 backdrop-blur-sm rounded-2xl p-8 border border-white/10">
              {(() => {
                const ActiveModuleIcon = modules[activeModule].icon;
                return (
                  <>
                    <div className="flex items-center gap-4 mb-6">
                      <div className="w-16 h-16 bg-gradient-to-br from-emerald-500 to-teal-600 rounded-2xl flex items-center justify-center">
                        <ActiveModuleIcon className="w-8 h-8 text-white" />
                      </div>
                      <h3 className="text-2xl font-bold">{modules[activeModule].title}</h3>
                    </div>
                    <ul className="space-y-4 text-slate-300">
                      {modules[activeModule].points.map((point, idx) => (
                        <li key={idx} className="flex items-start gap-3">
                          <div className="w-2 h-2 bg-emerald-400 rounded-full mt-2 flex-shrink-0"></div>
                          <span>{point}</span>
                        </li>
                      ))}
                    </ul>
                  </>
                );
              })()}

              <button className="mt-8 w-full py-3 bg-gradient-to-r from-emerald-500 to-teal-600 rounded-xl font-semibold hover:shadow-lg hover:shadow-emerald-500/50 transition-all flex items-center justify-center gap-2">
                En Savoir Plus
                <ChevronRight className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}
